var landsatCollection = ee.ImageCollection('LANDSAT/LC08/C02/T1')
  .filterDate('2017-01-01', '2017-12-31');

// Make a cloud-free composite.
var composite = ee.Algorithms.Landsat.simpleComposite({
  collection: landsatCollection,
